import unittest
from codicoMacrofitas.floradobrasil import urlFB, dadosFB, getInfoFlora, getSinonimosFB
from codicoMacrofitas.OperacoesArquivo import Writer
from codicoMacrofitas.macrofita import Macrofita
from codicoMacrofitas.floraInfo import FloraInfo

class TestPlantlist(unittest.TestCase):

    def testUrlFB(self, nomePlanta='Dicliptera ciliaris'):
        self.assertEqual(urlFB(nomePlanta), 'http://servicos.jbrj.gov.br/flora/taxon/Dicliptera%20ciliaris')
    
    def auxSetupRequisicaoFB(self,nomePlanta,tpRetorno):
        genero = nomePlanta.split(' ')[0]
        especie = nomePlanta.split(' ')[1]
        nome = genero + ' ' + especie
        retornoReqFB = self.opcoesRetorno(nomePlanta)
        macrofita = Macrofita(nomePlanta)
        escritorSinonimos = Writer('arquivo.xlsx', ['Nome das espécies - Status Flora = ACEITO', 'Sinônimos relevantes'])
        dadosFB(nome, retornoReqFB, macrofita)
        if (tpRetorno):
            return macrofita, retornoReqFB
        else:
            return macrofita

    def testDadosFBNaoEncontrado(self, nomePlanta='Eleocharis umbellata'):
        macrofita = self.auxSetupRequisicaoFB(nomePlanta, False)

        self.assertEqual(macrofita.statusFlora, '')
        self.assertEqual(macrofita.nomeFlora, '')
        self.assertEqual(macrofita.obsFlora, '')

    def testDadosFBNomeCorretoAutorCorreto(self, nomePlanta='Dicliptera ciliaris Juss.'):
        macrofita, retornoReqFB = self.auxSetupRequisicaoFB(nomePlanta, True)

        self.assertEqual(macrofita.statusFlora, 'Aceito')
        self.assertEqual(macrofita.nomeFlora, retornoReqFB['result'][0]['scientificname'])
        self.assertEqual(macrofita.obsFlora, '')

    def testDadosFBNomeCorretoAutorIncorreto(self, nomePlanta='Rhynchospora riparia (Nees) Boeck.'):
        macrofita, retornoReqFB = self.auxSetupRequisicaoFB(nomePlanta, True)

        self.assertEqual(macrofita.statusFlora, 'Aceito')
        self.assertEqual(macrofita.nomeFlora, retornoReqFB['result'][0]['scientificname'])
        self.assertEqual(macrofita.obsFlora, 'Autor Incorreto')

    def testDadosFBSinonimo(self, nomePlanta='Nelsonia brunelloides (Lam.) Kuntz'):
        macrofita, retornoReqFB = self.auxSetupRequisicaoFB(nomePlanta, True)

        self.assertEqual(macrofita.statusFlora, 'Sinonimo')
        self.assertEqual(macrofita.nomeFlora, retornoReqFB['result'][0]['NOME ACEITO'][0]['scientificname'])
        self.assertEqual(macrofita.obsFlora, '')

    def testDadosFBNaoPossuiNomeCorreto(self, nomePlanta='Scirpus cubensis  Poepp. & Kunth'):
        macrofita = self.auxSetupRequisicaoFB(nomePlanta, False)

        self.assertEqual(macrofita.statusFlora, 'Sinonimo')
        self.assertEqual(macrofita.nomeFlora, nomePlanta)
        self.assertEqual(macrofita.obsFlora, '')

    def testGetInfoFlora(self, nomePlanta='Steinchisma decipiens'):
        
        info = FloraInfo(nomePlanta)
        jsonResp = self.opcoesRetorno('floraInfo')
        getInfoFlora(nomePlanta, info, jsonResp)
        info.printInfo()
        self.assertEqual(info.taxonomica, 'Angiospermas')
        self.assertEqual(info.familia, 'Poaceae Barnhart')
        self.assertTrue(info.formaVida)
        self.assertTrue(info.substrato)
        self.assertEqual(info.origem, 'Nativa')
        self.assertEqual(info.endemismo, 'não é endêmica do Brasil')

        self.assertTrue(info.ocorenciaConfirmada)
        self.assertTrue(info.possiveisOcorrencias)
        self.assertTrue(info.fitogeografico)
        self.assertTrue(info.vegetacao)

    def testGetSinonimosFB(self):
        pass

